package Adapter;/** * @Author:zhenyuan * @Date:Created in 22:26 2018/12/27 */public class Banner {    private String string;    public Banner(String string) {        this.string = string;    }    public void showWithParen(){        System.out.println("("+string+")");    }    public void showWithAster(){        System.out.println("*"+string+"*");    }}