package Factory.idcard;import Factory.framework.Factory;import Factory.framework.Product;import java.util.ArrayList;import java.util.List;/** * @Author:zhenyuan * @Date:Created in 17:50 2018/12/29 */public class IDCardFactory extends Factory {    private List owners = new ArrayList();    @Override    public void registerProduct(Product p) {        owners.add(((IDCard) p).getOwner());    }    @Override    public Product createProduct(String ower) {        return new IDCard("yz");    }    public List getOwners() {        return owners;    }}