package Iterator;/** * @Author:zhenyuan * @Date:Created in 19:47 2018/12/26 */public class BookShelfIterator implements Iterator{    private BookShlf shelf;    private int index;    public BookShelfIterator(BookShlf shelf) {        this.shelf = shelf;        this.index = 0;    }    @Override    public boolean hasNext() {        return shelf.getLength()>index;    }    @Override    public Book next() {        return shelf.getBook(index++);    }}