package Iterator;/** * @Author:zhenyuan * @Date:Created in 19:44 2018/12/26 */public class BookShlf implements Aggregate {    private Book[] books;    private int last = 0;    public BookShlf(int maxSize) {        this.books = new Book[maxSize];    }    public Book getBook(int index) {        return books[index];    }    public void addBook(Book book) {        books[last] = book;        last++;    }    public int getLength(){        return last;    }    @Override    public Iterator iterator() {        return new BookShelfIterator(this);    }}