package Strategy;/** * @Author:zhenyuan * @Date:Created in 22:02 2019/1/5 */public class QuickSort implements Sort {    @Override    public void sort(Comparable[] data) {        System.out.println("quick sort");        mergeSort(data,0,data.length-1);    }    public void mergeSort(Comparable[] array,int l,int h){        if (h>l){            int mid = l+(h-l)/2;            mergeSort(array,l,mid);            mergeSort(array,mid+1,h);            sort(array,l,h);        }        return ;    }    private void sort(Comparable[] a,int l,int h){        int length = h-l+1, mid = l+(h-l)/2,index=0,l1=mid+1,low = l;        Comparable[] aus = new Comparable[length];        while (l<=mid&l1<=h){            if ((a[l].compareTo(a[l1])<0)){ aus[index++]=a[l++];}            else {aus[index++]=a[l1++];}        }        while (l1<=h){            aus[index++]=a[l1++];        }        while (l<=mid){            aus[index++]=a[l++];        }        for (int i=0;i<length;i++){            a[i+low]=aus[i];        }    }}