package Strategy;/** * @Author:zhenyuan * @Date:Created in 21:56 2019/1/5 */public class SelectSorter implements Sort {    @Override    public void sort(Comparable[] data) {        System.out.println("select sort");        for (int i = 0; i < data.length - 1; i++) {            int min =1;            for (int j = i+1; j < data.length; j++) {                if (data[min].compareTo(data[j])>0){                    min = j;                }            }            Comparable temp = data[min];            data[min] = data[i];            data[i] = temp;        }    }}