package Strategy;/** * @Author:zhenyuan * @Date:Created in 22:00 2019/1/5 */public class UseData {    private Sort sort;    Comparable[] data;    public UseData(Sort sort, Comparable[] data) {        this.sort = sort;        this.data = data;    }    public void excute(){        print();        sort.sort(data);        print();    }    private void print() {        for (int i = 0; i < data.length; i++) {            System.out.print(data[i]+" ");        }        System.out.println();    }}