package Template;/** * @Author:zhenyuan * @Date:Created in 22:17 2018/12/28 */public class CharDisplay extends AbstractDisplay {    private char ch;    public CharDisplay(char c) {        this.ch = c;    }    @Override    public void open() {        System.out.print("<<");    }    @Override    public void print() {        System.out.print(ch);    }    @Override    public void close() {        System.out.println(">>");    }}