package Template;/** * @Author:zhenyuan * @Date:Created in 22:17 2018/12/28 */public class StringDisplay extends AbstractDisplay {    private int width;    private String string;    public StringDisplay(String c) {        this.string = c;        this.width = c.getBytes().length;    }    @Override    public void open() {        printLine();    }    @Override    public void print() {        System.out.println("|"+string+"|");    }    @Override    public void close() {        printLine();    }    public void printLine(){        System.out.print("+");        for (int i = 0; i < width; i++) {            System.out.print("-");        }        System.out.println("+");    }}