package bridge;/** * @Author:zhenyuan * @Date:Created in 21:00 2019/1/4 */public class StringDisplayImpl extends DisplayImpl{    private String string;    private int width;    public StringDisplayImpl(String string) {        this.string = string;        this.width = string.getBytes().length;    }    @Override    public void rawOpen() {        System.out.println("opening...");    }    @Override    public void rawPrint() {        System.out.println("printint...");    }    @Override    public void rawClose() {        System.out.println("closeing...");    }}