package composite;import java.lang.reflect.Array;import java.util.ArrayList;import java.util.Iterator;/** * @Author:zhenyuan * @Date:Created in 20:27 2019/1/6 */public class Directory extends Entry {    private String name;    private ArrayList directory = new ArrayList();    public Directory(String name) {        this.name = name;    }    @Override    public String getName() {        return name;    }    @Override    public int getSize() {        int size = 0;        Iterator iterator = directory.iterator();        while (iterator.hasNext()){            Entry entry = (Entry) iterator.next();            size += entry.getSize();        }        return size;    }    @Override    public void printList() {    }}