package decorator;/** * @Author:zhenyuan * @Date:Created in 19:31 2019/1/7 */public class FullBorder extends Border {    public FullBorder(Dispaly dispaly) {        super(dispaly);    }    @Override    public int getColumns() {        return 2+dispaly.getColumns();    }    @Override    public int getRows() {        return dispaly.getRows()+2;    }    @Override    public String getRowText(int row) {        if (row  == 0){            return "+"+makeLine(dispaly.getColumns(),"-")+"+";        }else if (row == dispaly.getRows()+1){            return "+"+makeLine(dispaly.getColumns(),"-")+"+";        }        return "|"+dispaly.getRowText(row-1)+"|";    }    private String makeLine(int columns, String s) {        StringBuffer stringBuffer = new StringBuffer();        for (int i = 0; i < columns; i++) {            stringBuffer.append(s);        }        return stringBuffer.toString();    }}