package decorator;/** * @Author:zhenyuan * @Date:Created in 19:31 2019/1/7 */public class SideBorder extends Border {    private char borderChar;    public SideBorder(Dispaly dispaly, char borderChar) {        super(dispaly);        this.borderChar = borderChar;    }    @Override    public int getColumns() {        return 2+dispaly.getColumns();    }    @Override    public int getRows() {        return dispaly.getRows();    }    @Override    public String getRowText(int row) {        return borderChar+dispaly.getRowText(row)+borderChar;    }}