package decorator;/** * @Author:zhenyuan * @Date:Created in 19:27 2019/1/7 */public class StringDisplay extends Dispaly {    String string;    public StringDisplay(String string) {        this.string = string;    }    @Override    public int getColumns() {        return string.length();    }    @Override    public int getRows() {        return 1;    }    @Override    public String getRowText(int row) {        if (row == 0){            return string;        }        return null;    }}