package observer;import java.util.ArrayList;import java.util.Iterator;/** * @Author:zhenyuan * @Date:Created in 22:23 2019/1/13 */public abstract class Generator {    private ArrayList<Observer> observers = new ArrayList<>();    public void addObserver(Observer observer){        observers.add(observer);    }    public void notifyObserber(){        Iterator<Observer> iterator = observers.iterator();        while (iterator.hasNext()){            Observer observer = iterator.next();            observer.update(this);        }    }    public abstract int getNum();    public abstract void execute();}