package observer;import java.util.Random;/** * @Author:zhenyuan * @Date:Created in 22:28 2019/1/13 */public class RandomGenerator extends Generator{    private Random random = new Random();    private int num;    @Override    public int getNum() {        return num;    }    @Override    public void execute() {        num = random.nextInt(50);        notifyObserber();    }}