package prototype.framework;import java.util.HashMap;/** * @Author:zhenyuan * @Date:Created in 19:27 2018/12/30 */public class Manager {    public HashMap showCase = new HashMap();    public void register(String s, Product product) {        showCase.put(s, product);    }    public Product create(String s) {        Product product = (Product) showCase.get(s);        return product.createClone();    }}