package prototype.use;import prototype.framework.Product;/** * @Author:zhenyuan * @Date:Created in 19:35 2018/12/30 */public class MessageBox implements Product {    private char decochar;    public MessageBox(char decochar) {        this.decochar = decochar;    }    @Override    public void use(String s) {        int length = s.getBytes().length;        for (int i = 0; i < (length + 4) / 2; i++) {            System.out.print(decochar + " ");        }        System.out.print(decochar);        System.out.println("");        System.out.println(decochar + " " + s + " " + decochar);        for (int i = 0; i < (length + 4) / 2; i++) {            System.out.print(decochar + " ");        }        System.out.print(decochar);        System.out.println("");    }    @Override    public Product createClone() {        Product product = null;        try {            product = (Product) clone();        } catch (CloneNotSupportedException e) {            e.printStackTrace();        }        return product;    }}